mapper.update:
UPDATE JERRY_POST SET TITLE = #{title}, SUBJECT = #{subject}, DESCRIPTION = #{description}, TAGS = #{tags}, STATUS = #{status},
POSTIMAGE = #{postImage},
MARKDOWNCONTENT = #{markdownContent}, HTMLCONTENT = #{htmlContent} WHERE ID = #{id}

    	UPDATE JERRY_POST_LIKE
		SET IS_LIKE = #{like}
		WHERE POSTID = #{postId}
		AND LIKE_USER = #{userId}
